    constructor(props){
        super(props);
        this.state = {
		user:'',
		password:''
	}
    }

    login(){
	let ServerAddr = 'http://ec2-52-32-190-25.us-west-2.compute.amazonaws.com:4001'   
    }

    componentDidMount() {
        axios.post(this.state.ServerAddr+'/login')
        .then( res => {
		console.log(res);
		/*여기에 로그인 성공시메인으로 자동  화면 전환 해야하는 코드 추가 할 것*/
	})
        .catch( res => { 
		console.log(res);
        });
    }

    handleSubmit = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    render(){
        return(
	<Container>
            <Form inline onSubmit={this.handleSubmit}>

<FormGroup>
                    <Label for="ID">Email</Label>
                    <Input type="text" name="id" id="ID" placeholder="ID" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="password">Password</Label>
                    <Input type="password" name="password" id="password" placeholder="Password" onChange={this.handleChange}/>
                </FormGroup>
            </Form>
	</Container>
        );
    }
}

export default Login;
