import React, { Component } from 'react';
import { Container, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class SignUp extends Component{
    constructor(props){
	super(props);
	this.toggle = this.toggle.bind(thid);
    }
    state = {
        name: '',
        email: '',
        id: '',
        password: '',
        role: '구매자',
	strCheckId: '',
	checkPassword: '',
	boolCheckId:false
    }
    toggle(){
	this.setState({boolCheckId: !this.state.boolCheckId});
    }
    checkId(){
	console.log(this.state.id)
	let spe = this.state.id.search(/['~!@@#$%^&*|\\\'\'';:\/?.,-_+=`]/);
	console.log(spe)
	if(spe >= 0){
		this.setState({strCheckId:"아이디는 영문으로만 이루어져야 합니다."});
		return false
	}
	this.setState({strCheckId:""});
	return true
    }
    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state);
        this.setState({
            name: '',
            mail: '',
            id: '',
            password: '',
            role: '',
        })
	this.checkId()
    }
    render() {
        return (
	<Container>
            <div>
            <Form onSubmit={this.handleSubmit}>
                <h1>회원가입</h1>
                <FormGroup>
                    <Label for="ID">Id</Label>
                    <Input type="text" name="id" id="ID" placeholder="ID" onChange={this.handleChange}/>
		    <Tooltip placement="bottom" isOpen={this.state.boolcheckId} target="ID" toggle={this.toggle}>id error!</Tooltip>
                </FormGroup>
                <FormGroup>
                    <Label for="password">Password</Label>
                    <Input type="password" name="password" id="password" placeholder="Password" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="password">Password Check</Label>
                    <Input type="password" name="password" id="password" placeholder="Password" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="name">Name</Label>
                    <Input type="text" name="name" id="name" placeholder="name" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="email">Email</Label>
                    <Input type="email" name="email" id="email" placeholder="예시) zongog@ajou.ac.kr" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="role">Select</Label>
                    <Input type="select" name="role" id="role" onChange={this.handleChange}>
                        <option>구매자</option>
                        <option>판매자</option>
                    </Input>
                </FormGroup>
                <Button>가입</Button>

            </Form>
                { /*<h5>{this.state.id}</h5>
                <h5>{this.state.password}</h5>
                <h5>{this.state.name}</h5>
                <h5>{this.state.email}</h5>
                <h5>{this.state.role}</h5> */}
            </div>
	</Container>
        );
    }
}


export default SignUp;
