import React, { Component } from 'react';
import axios from "axios";
import { Container, Row, Col, Button, Form, FormGroup, Label, Input } from 'reactstrap';

class Login extends Component{
    constructor(props){
        super(props);
        this.state = {
		user:'',
		password:''
	}
    }

    login(){
	let ServerAddr = 'http://ec2-52-32-190-25.us-west-2.compute.amazonaws.com:4001'   
    }

    componentDidMount() {
        axios.post(this.state.ServerAddr+'/login')
        .then( res => {
		console.log(res);
		/*여기에 로그인 성공시메인으로 자동  화면 전환 해야하는 코드 추가 할 것*/
	})
        .catch( res => { 
		console.log(res);
        });
    }

    handleSubmit = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    render(){
        return(
            <Form inline onSubmit={this.handleSubmit}>
		<Row form>
		<Col>
                <FormGroup>
                    <Label for="ID" className="mr-sm-2"><h2>ID</h2></Label>
                    <Input type="text" name="checkId"/>
                </FormGroup>
		</Col>
		</Row>
		<Row>
		<Col>
                <FormGroup>
                    <Label for="Password" className="mr-sm-2"><h2>Password</h2></Label>
                    <Input type="password" name="checkPassword"/>
                </FormGroup>
		</Col>
		</Row>
		<Row>
		<Col>
                <Button type = "submit">Sign in</Button>
		</Col>
		</Row>
            </Form>
        );
    }
}

export default Login;
